package ex_04;

public class Ex04_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 99;
		
		System.out.printf(" AND 연산 : %s \n", (a >= 100) && (a <= 200));
		System.out.printf(" AND 연산 : %s \n", (a >= 100) || (a <= 200));
		System.out.printf(" AND 연산 : %s \n", !(a == 100));
		
	}

}
